import { motion } from 'framer-motion';
import './Social.css';

const Social = () => {
  const socialLinks = [
    { name: 'GitHub', url: 'https://github.com/yourusername', icon: 'fab fa-github' },
    { name: 'LinkedIn', url: 'https://linkedin.com/in/yourusername', icon: 'fab fa-linkedin' },
    { name: 'Twitter', url: 'https://twitter.com/yourusername', icon: 'fab fa-twitter' },
    { name: 'Medium', url: 'https://medium.com/@yourusername', icon: 'fab fa-medium' },
    // Add more social links
  ];

  return (
    <motion.section
      className="social-section"
      initial={{ scale: 0.8, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      exit={{ scale: 0.8, opacity: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2>Connect With Me</h2>
      <div className="social-grid">
        {socialLinks.map((social, index) => (
          <motion.a
            key={index}
            href={social.url}
            target="_blank"
            rel="noopener noreferrer"
            className="social-card"
            whileHover={{ y: -5 }}
            whileTap={{ scale: 0.95 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <i className={`${social.icon} social-icon`}></i>
            <span>{social.name}</span>
          </motion.a>
        ))}
      </div>
      
      <div className="github-stats">
        {/* You can add GitHub stats using GitHub API */}
        <img 
          src="https://github-readme-stats.vercel.app/api?username=yourusername&show_icons=true&theme=dracula" 
          alt="GitHub Stats" 
        />
      </div>
    </motion.section>
  );
};

export default Social;
